    IF @ActorID = @UserID
    RETURN 1

    DECLARE @IsPeer bit
    DECLARE @OwnerID int

    SELECT @IsPeer = IsPeer, @OwnerID = OwnerID FROM Users
    WHERE UserID = @ActorID

