    	-- get owner
    	SELECT
    		@ParentUserID = OwnerID
    	FROM Users
    	WHERE UserID = @TmpUserID

    	IF @ParentUserID IS NULL -- the last parent
    		BREAK

    	IF @ParentUserID = @ActorID
    	RETURN 1

    	SET @TmpUserID = @ParentUserID
    END

    RETURN 0
    END'
END;
