    )
    AS
    BEGIN

    	IF EXISTS (SELECT * FROM SupportServiceLevels WHERE LevelName = @LevelName)
    	BEGIN
    		SET @LevelID = -1

    		RETURN
    	END

    	INSERT INTO SupportServiceLevels
    	(
    		LevelName,
    		LevelDescription
    	)
    	VALUES
    	(
    		@LevelName,
    		@LevelDescription
    	)

