    	FROM PackageIPAddresses AS PIP
    	INNER JOIN Packages AS P ON PIP.PackageID = P.PackageId
    	WHERE PIP.PackageAddressID = @PackageAddressID

    	IF (@ParentPackageID = 1) -- ""System"" space
    	BEGIN
