    		WHERE PackageAddressID = @PackageAddressID
    	END
    	ELSE -- 2rd level space and below
    	BEGIN
    		UPDATE PackageIPAddresses
    		SET PackageID = @ParentPackageID
    		WHERE PackageAddressID = @PackageAddressID
    	END

    END'
END;
