                RETURN -1
          END

    -- delete user
    DELETE FROM Users
    WHERE UserID = @UserID

    IF (@@ERROR <> 0 )
          BEGIN
                ROLLBACK TRANSACTION
                RETURN -1
          END

    COMMIT TRAN

    RETURN'
END;
