        from Users
        where UserID = @ActorID
        union all
        select C.UserId
        from GetChildUsersId P
        inner join Users C on P.id = C.OwnerID
    )

    SELECT 
    	T.ID,
    	T.Guid,
    	T.TaskID,
    	T.ScheduleId,
    	T.PackageId,
    	T.UserId,
